        #region /// 비디오 녹화 ///

        private static void StartRecord(System.Drawing.Rectangle recordingArea, string outputFileName)
        {
            try
            {
                // 녹화기 초기화
                screenCaptrue = new ScreenCaptureStream(recordingArea);
                Source.Log.log.Info("screenCapture = new ScreenCaptureStream(recordingArea);");
                videoWriter = new VideoFileWriter();
                Source.Log.log.Info("videoWriter = new VideoFileWriter();");

                // 출력 설정
                videoWriter.Open(outputFileName, recordingArea.Width, recordingArea.Height, 30, VideoCodec.H264, 1000000);
                Source.Log.log.Info("videoWriter.Open(outputFileName, recordingArea.Width, recordingArea.Height, 30, VideoCodec.H264, 1000000);");

                // 프레임 캡처 이벤트 핸들러 등록
                screenCaptrue.NewFrame += ScreenCapture_NewFrame;

                // 녹화 시작
                screenCaptrue.Start();
                isRecording = true;
                Console.WriteLine("녹화 시작");
            }
            catch (Exception ex)
            {
                Source.Log.log.Error(MethodBase.GetCurrentMethod().Name + "() - " + ex.Message);
            }
        }

        private static void StopRecord()
        {
            try
            {
                if (!isRecording)
                    return;

                // 이벤트 핸들러 해제
                screenCaptrue.NewFrame -= ScreenCapture_NewFrame;

                // 녹화 종료
                screenCaptrue.SignalToStop();
                screenCaptrue.WaitForStop();

                // 파일 닫기
                videoWriter.Close();
                videoWriter.Dispose();
                videoWriter = null;

                isRecording = false;
                Console.WriteLine("녹화 종료");
            }
            catch (Exception ex)
            {
                Source.Log.log.Error(MethodBase.GetCurrentMethod().Name + "() - " + ex.Message);
            }
        }

        private static void ScreenCapture_NewFrame(object sender, NewFrameEventArgs eventArgs)
        {
            try
            {
                // 프레임 캡처 및 저장
                if (isRecording)
                {
                    Bitmap frame = (Bitmap)eventArgs.Frame.Clone();
                    videoWriter.WriteVideoFrame(frame);
                    frame.Dispose();
                }
            }
            catch (Exception ex)
            {
                Source.Log.log.Error(MethodBase.GetCurrentMethod().Name + "() - " + ex.Message);
            }
        }

